						toggleFrame("CEPGP_options");
						mode = "options";
						CEPGP_options_mod_edit:SetText(tostring(MOD));
						CEPGP_options_coef_edit:SetText(tostring(COEF));
						CEPGP_options_gp_base_edit:SetText(tostring(BASEGP));
						FORMULA = "GP Formula: "..tostring(COEF).."*(2^(ilvl/26+(rarity-4)))*slot)*"..tostring(MOD);
						for k, v in pairs(AUTOEP) do
							if k == "lord kri" or k == "vem" or k == "princess yauj" then
								getglobal("CEPGP_options_the bug trio_auto_check"):SetChecked(v);
							elseif k == "emperor vek'lor" or k == "emperor vek'nilash" then
								getglobal("CEPGP_options_twin emps_auto_check"):SetChecked(v);
							elseif k == "highlord mograine" or k == "thane korth'azz" or k == "lady blaumeux" or k == "sir zeliek" then
								getglobal("CEPGP_options_four horsemen_auto_check"):SetChecked(v);
							elseif k == "gri'lek" or k == "hazza'rah" or k == "renataki" or k == "wushoolay" then
								getglobal("CEPGP_options_edge of madness_auto_check"):SetChecked(v);
							else
								getglobal("CEPGP_options_"..k.."_auto_check"):SetChecked(v);
							end

						end
						for k, v in pairs(EPVALS) do
							if k == "lord kri" or k == "vem" or k == "princess yauj" then
								getglobal("CEPGP_options_the bug trio_EP_value"):SetText(v);
							elseif k == "emperor vek'lor" or k == "emperor vek'nilash" then
								getglobal("CEPGP_options_twin emps_EP_value"):SetText(v);
							elseif k == "highlord mograine" or k == "thane korth'azz" or k == "lady blaumeux" or k == "sir zeliek" then
								getglobal("CEPGP_options_four horsemen_EP_value"):SetText(v);
							elseif k == "gri'lek" or k == "hazza'rah" or k == "renataki" or k == "wushoolay" then
								getglobal("CEPGP_options_edge of madness_EP_value"):SetText(v);
							else
								getglobal("CEPGP_options_"..k.."_EP_value"):SetText(v);
							end
						end
						CEPGP_options_formula:SetText(FORMULA);
						populateFrame();

