if CEPGP_options:IsVisible() then
	CEPGP_options:Hide();
	CEPGP_options_page_2:Show();
else
	CEPGP_options:Show();
	CEPGP_options_page_2:Hide();
end

PlaySound("gsTitleOptionExit");
CEPGP_options_coef_edit:SetText("0.483");
CEPGP_options_mod_edit:SetText("1");
CEPGP_options_gp_base_edit:SetText("1");
FORMULA = "GP Formula: "..tostring(COEF).."*(2^(ilvl/26+(rarity-4)))*slot)*"..tostring(MOD);
CEPGP_options_formula:SetText(FORMULA);
CEPGP_print("GP settings have been reset to default");

PlaySound("gsTitleOptionExit");
if 0 >= tonumber(CEPGP_options_mod_edit:GetText()) then
	CEPGP_print("Invalid number entered for GP modifier. Please enter a positive number", true);
else
	MOD = tonumber(CEPGP_options_mod_edit:GetText());
end
if 0 >= tonumber(CEPGP_options_coef_edit:GetText()) then
	CEPGP_print("Invalid number entered for base coefficient. Please enter a positive number", true);
else
	COEF = tonumber(CEPGP_options_coef_edit:GetText());
end
if 0 >= tonumber(CEPGP_options_gp_base_edit:GetText()) then
	if tonumber(CEPGP_options_gp_base_edit:GetText()) == 0 then
		CEPGP_print("Base GP cannot be 0!", true);
	else
		CEPGP_print("Base GP must be a positive number", true);
	end
else
	BASEGP = tonumber(CEPGP_options_gp_base_edit:GetText());
end
FORMULA = "GP Formula: "..tostring(COEF).."*(2^(ilvl/26+(rarity-4)))*slot)*"..tostring(MOD);
CEPGP_options_formula:SetText(FORMULA);
CEPGP_options_mod_edit:ClearFocus();
CEPGP_options_coef_edit:ClearFocus();
CEPGP_options_gp_base_edit:ClearFocus();
SetGuildRosterShowOffline(true);
GuildRoster();
if CanEditOfficerNote() == 1 then
	for i = 1, GetNumGuildMembers() do
		local name, rank, rankIndex, _, class, _, _, officerNote = GetGuildRosterInfo(i);
		if officerNote then
			local EP, GP = getEPGP(officerNote);
			if BASEGP > GP then
				GuildRosterSetOfficerNote(i, EP .. "," .. BASEGP);
			end
		end
		if name then
			roster[name] = {
			[1] = i,
			[2] = class,
			[3] = rank,
			[4] = rankIndex,
			[5] = officerNote
			};
		end
	end
end

