for k, v in pairs(EPVALS) do
	if k == "lord kri" or k == "vem" or k == "princess yauj" then
		EPVALS[k] = getglobal("CEPGP_options_the bug trio_EP_value"):GetText();
		getglobal("CEPGP_options_the bug trio_EP_value"):ClearFocus();
	elseif k == "emperor vek'lor" or k == "emperor vek'nilash" then
		EPVALS[k] = getglobal("CEPGP_options_twin emps_EP_value"):GetText();
		getglobal("CEPGP_options_twin emps_EP_value"):ClearFocus();
	elseif k == "highlord mograine" or k == "thane korth'azz" or k == "lady blaumeux" or k == "sir zeliek" then
		EPVALS[k] = getglobal("CEPGP_options_four horsemen_EP_value"):GetText();
		getglobal("CEPGP_options_four horsemen_EP_value"):ClearFocus();
	elseif k == "gri'lek" or k == "hazza'rah" or k == "renataki" or k == "wushoolay" then
		EPVALS[k] = getglobal("CEPGP_options_edge of madness_auto_check"):GetText();
	else
		EPVALS[k] = getglobal("CEPGP_options_"..k.."_EP_value"):GetText();
		getglobal("CEPGP_options_"..k.."_EP_value"):ClearFocus();
	end
end

for k, v in pairs(AUTOEP) do
	if k == "lord kri" or k == "vem" or k == "princess yauj" then
		if getglobal("CEPGP_options_the bug trio_auto_check"):GetChecked() then
			AUTOEP[k] = true;
		else
			AUTOEP[k] = false;
		end
	elseif k == "emperor vek'lor" or k == "emperor vek'nilash" then
		if getglobal("CEPGP_options_twin emps_auto_check"):GetChecked() then
			AUTOEP[k] = true;
		else
			AUTOEP[k] = false;
		end
	elseif k == "highlord mograine" or k == "thane korth'azz" or k == "lady blaumeux" or k == "sir zeliek" then
		if getglobal("CEPGP_options_four horsemen_auto_check"):GetChecked() then
			AUTOEP[k] = true;
		else
			AUTOEP[k] = false;
		end
	elseif k == "gri'lek" or k == "hazza'rah" or k == "renataki" or k == "wushoolay" then
		if getglobal("CEPGP_options_edge of madness_auto_check"):GetChecked() then
			AUTOEP[k] = true;
		else
			AUTOEP[k] = false;
		end
	else
		if getglobal("CEPGP_options_"..k.."_auto_check"):GetChecked() then
			AUTOEP[k] = true;
		else
			AUTOEP[k] = false;
		end
	end
end

if this:GetChecked() == 1 then
	CEPGP_context_popup_GP_check:SetChecked(nil);
	CEPGP_context_popup_desc:SetText("Adding EP");
else
	this:SetChecked(1);
end

