Scripts.OnClick:
local name = CEPGP_guild_log_restore_name:GetText();
GuildRoster();
if RECORDS[name] ~= nil and CEPGP_confirmrestore then
	if GetGuildRosterShowOffline() == nil then
		SetGuildRosterShowOffline(true);
		for i = 1, GetNumGuildMembers(), 1 do
			if RECORDS[name][GetGuildRosterInfo(i)] == nil then
				GuildRosterSetOfficerNote(i, "0,"..BASEGP);
			else
				GuildRosterSetOfficerNote(i, RECORDS[name][GetGuildRosterInfo(i)]);
			end
		end
		SetGuildRosterShowOffline(false);
		CEPGP_print("Guild EPGP standings have been restored from record [" .. name .. "]");
		CEPGP_confirmrestore = false;
		HideUIPanel(CEPGP_restore_guild_logs);
	else
		for i = 1, GetNumGuildMembers(), 1 do
			if RECORDS[name][GetGuildRosterInfo(i)] == nil then
				GuildRosterSetOfficerNote(i, "0,"..BASEGP);
			else
				GuildRosterSetOfficerNote(i, RECORDS[name][GetGuildRosterInfo(i)]);
			end
		end
		CEPGP_print("Guild EPGP standings have been restored from record [" .. name .. "]");
		CEPGP_confirmrestore = false;
		HideUIPanel(CEPGP_restore_guild_logs);
	end
elseif RECORDS[name] ~= nil then
	CEPGP_print("Are you sure you would like to restore guild EPGP standings from record [" .. name .. "]?");
	CEPGP_print("This action CANNOT be undone. Press confirm again to restore.");
	CEPGP_confirmrestore = true;
else
	CEPGP_print("Record [" .. name .. "] could not be found", true);
end

local CEPGP_target = CEPGP_settings_import_name:GetText();
if CEPGP_raidRoster[CEPGP_target] then
	CEPGP_SendAddonMsg(CEPGP_target .. "-import", "RAID");
elseif CEPGP_roster[CEPGP_target] then
	CEPGP_SendAddonMsg(CEPGP_target .. "-import", "GUILD");
else
	CEPGP_print(CEPGP_target .. " not found in guild or raid group", true);
end
CEPGP_settings_import_name:ClearFocus();
HideUIPanel(CEPGP_settings_import);

