	gp = 0;
else
	gp = tonumber(CEPGP_CEPGP_override_item_gp:GetText());
end
if (OVERRIDE_INDEX[item] and overwrite) or not OVERRIDE_INDEX[item] then
	OVERRIDE_INDEX[item] = gp;
	CEPGP_print("GP value for " .. CEPGP_CEPGP_override_item_name:GetText() .. " has been CEPGP_overriden to " .. gp);
	CEPGP_UpdateOverrideScrollBar();
	CEPGP_override = false;
	CEPGP_CEPGP_override_item_name:SetText("");
	CEPGP_CEPGP_override_item_gp:SetText("");
	CEPGP_CEPGP_override_item_name:SetFocus();
else
	CEPGP_print(CEPGP_CEPGP_override_item_name:GetText() .. " has already been overridden to " .. OVERRIDE_INDEX[item] .. ". Click add CEPGP_override again to change this to " .. gp);
	CEPGP_override = true;
end

Scripts.OnClick:
PlaySound("gsTitleOptionExit");
if CEPGP_CEPGP_override_item_name:GetText() == "" then
		CEPGP_print("Enter an item name", true);
	return;
end
local item = CEPGP_CEPGP_override_item_name:GetText();
local gp;
if CEPGP_CEPGP_override_item_gp:GetText() == "" then
	gp = 0;
else
	gp = tonumber(CEPGP_CEPGP_override_item_gp:GetText());
end
if (OVERRIDE_INDEX[item] and overwrite) or not OVERRIDE_INDEX[item] then
	OVERRIDE_INDEX[item] = gp;
	CEPGP_print("GP value for " .. CEPGP_CEPGP_override_item_name:GetText() .. " has been CEPGP_overriden to " .. gp);
	CEPGP_UpdateOverrideScrollBar();
	CEPGP_override = false;
	CEPGP_CEPGP_override_item_name:SetText("");
	CEPGP_CEPGP_override_item_name:ClearFocus();
	CEPGP_CEPGP_override_item_gp:SetText("");
	CEPGP_CEPGP_override_item_gp:ClearFocus();
else
	CEPGP_print(CEPGP_CEPGP_override_item_name:GetText() .. " has already been overridden to " .. OVERRIDE_INDEX[item] .. ". Click add CEPGP_override again to change this to " .. gp);
	CEPGP_override = true;
end

Scripts.OnLoad:
this:RegisterForDrag("LeftButton");
CEPGP_CEPGP_override_desc:SetText("This window allows your to permanently\n(or until the log is removed), CEPGP_override the gp\nvalue of an item to what ever you specify");
_, _, _, CEPGP_pfUIFrames = GetAddOnInfo("pfUI");
if CEPGP_pfUIFrames then
	CEPGP_CEPGP_override_item_name:SetPoint("TOP", CEPGP_CEPGP_override_item_name_string, "BOTTOM", 10, 0);
	CEPGP_CEPGP_override_item_gp:SetPoint("TOP", CEPGP_CEPGP_override_item_gp_string, "BOTTOM", 0, 0);
end

Scripts.OnClick:
PlaySound("gsTitleOptionExit");
if CEPGP_traffic_clear then
	TRAFFIC = {};
	CEPGP_UpdateTrafficScrollBar();
	CEPGP_print("EPGP traffic wiped");
	CEPGP_traffic_clear = false;
else
	CEPGP_print("You are trying to clear your EPGP traffic log. This action cannot be reversed.");
	CEPGP_print("Press Clear EPGP traffic again to confirm");
	CEPGP_traffic_clear = true;
end

Scripts.OnLoad:
this:RegisterForDrag("LeftButton");
_, _, _, CEPGP_pfUIFrames = GetAddOnInfo("pfUI");
if CEPGP_pfUIFrames then
	for i = 1, 18 do
		getglobal("trafficButton" .. i .. "Action"):SetPoint("RIGHT", getglobal("trafficButton" .. i .. "Name"), "LEFT", 265, 0);
		getglobal("trafficButton" .. i .. "ItemName"):SetPoint("LEFT", getglobal("trafficButton" .. i .. "Name"), "LEFT", 280, 0);
		getglobal("trafficButton" .. i .. "EPBefore"):SetPoint("CENTER", getglobal("trafficButton" .. i .. "Name"), "LEFT", 530, 0);
		getglobal("trafficButton" .. i .. "EPAfter"):SetPoint("CENTER", getglobal("trafficButton" .. i .. "Name"), "LEFT", 570, 0);
		getglobal("trafficButton" .. i .. "GPBefore"):SetPoint("CENTER", getglobal("trafficButton" .. i .. "Name"), "LEFT", 640, 0);
		getglobal("trafficButton" .. i .. "GPAfter"):SetPoint("CENTER", getglobal("trafficButton" .. i .. "Name"), "LEFT", 680, 0);
	end
end
