Scripts.OnClick:
                        PlaySound("gsTitleOptionExit");
						CEPGP_guild:Hide();
						CEPGP_raid:Hide();
						CEPGP_loot:Show();
						CEPGP_distribute:Hide();
						mode = "loot";
						cleanTable();
