Scripts.OnClick:
if this:GetChecked() == 1 then
	CEPGP_context_popup_GP_check:SetChecked(nil);
	CEPGP_context_popup_desc:SetText("Adding EP");
else
	this:SetChecked(1);
end

if this:GetChecked() == 1 then
	CEPGP_context_popup_EP_check:SetChecked(nil);
	CEPGP_context_popup_desc:SetText("Adding GP");
else
	this:SetChecked(1);
end
